PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
DROP TABLE IF EXISTS doctrine_migration_versions;
DROP TABLE IF EXISTS book;
DROP TABLE IF EXISTS messenger_messages;
DROP TABLE IF EXISTS escape_room;
DROP TABLE IF EXISTS escape_action;
DROP TABLE IF EXISTS escape_object;
DROP TABLE IF EXISTS escape_movement;
CREATE TABLE doctrine_migration_versions (version VARCHAR(191) NOT NULL, executed_at DATETIME DEFAULT NULL, execution_time INTEGER DEFAULT NULL, PRIMARY KEY(version));
CREATE TABLE book (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, title VARCHAR(45) NOT NULL, isbn VARCHAR(20) DEFAULT NULL, author VARCHAR(55) NOT NULL, img VARCHAR(100) DEFAULT NULL);
CREATE TABLE messenger_messages (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, body CLOB NOT NULL, headers CLOB NOT NULL, queue_name VARCHAR(190) NOT NULL, created_at DATETIME NOT NULL, available_at DATETIME NOT NULL, delivered_at DATETIME DEFAULT NULL);
CREATE TABLE escape_room (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, info CLOB DEFAULT NULL, img VARCHAR(100) DEFAULT NULL, first_room BOOLEAN DEFAULT NULL);
CREATE TABLE escape_action (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, name VARCHAR(25) DEFAULT NULL);
CREATE TABLE escape_object (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, in_room INTEGER DEFAULT NULL, action_id INTEGER DEFAULT NULL, name VARCHAR(25) DEFAULT NULL, info CLOB DEFAULT NULL, position_x INTEGER DEFAULT NULL, position_y INTEGER DEFAULT NULL, img VARCHAR(55) DEFAULT NULL, size_x INTEGER DEFAULT NULL, size_y INTEGER DEFAULT NULL, in_object INTEGER DEFAULT NULL, info_inner CLOB DEFAULT NULL, hidden BOOLEAN DEFAULT NULL, FOREIGN KEY (in_room) REFERENCES escape_room (id) ON UPDATE NO ACTION ON DELETE NO ACTION NOT DEFERRABLE INITIALLY IMMEDIATE, FOREIGN KEY (action_id) REFERENCES escape_action (id) ON UPDATE NO ACTION ON DELETE NO ACTION NOT DEFERRABLE INITIALLY IMMEDIATE);
CREATE TABLE escape_movement (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, room_id INTEGER NOT NULL, to_room INTEGER NOT NULL, position_x INTEGER DEFAULT NULL, position_y INTEGER DEFAULT NULL, direction VARCHAR(10) DEFAULT NULL, FOREIGN KEY (room_id) REFERENCES escape_room(id));
DELETE FROM sqlite_sequence;
CREATE INDEX IDX_75EA56E0FB7336F0 ON messenger_messages (queue_name);
CREATE INDEX IDX_75EA56E0E3BD61CE ON messenger_messages (available_at);
CREATE INDEX IDX_75EA56E016BA31DB ON messenger_messages (delivered_at);
CREATE INDEX IDX_1C351DA758246EFF ON escape_object (in_room);
CREATE INDEX IDX_1C351DA79D32F035 ON escape_object (action_id);
COMMIT;
